mapper.select:
SELECT USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, USER_JOIN_DATE FROM (SELECT ROWNUM AS RNUM, USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, USER_JOIN_DATE FROM ( SELECT USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, USER_JOIN_DATE FROM TBL_USER ) S1
WHERE ROWNUM <= #{page} * #{amount}
) S2 WHERE RNUM > (#{page} - 1) * #{amount}

SELECT USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, TO_CHAR(USER_JOIN_DATE, 'yyyy-mm-dd') USER_JOIN_DATE FROM( SELECT ROWNUM AS RNUM, USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, USER_JOIN_DATE FROM( SELECT USER_NUMBER, USER_NAME, USER_BIRTH, USER_ID, USER_GENDER, USER_PHONE_NUMBER, USER_ADDRESS1, USER_EMAIL, USER_JOIN_DATE FROM TBL_USER where 1 = 1
and USER_NAME like '%'|| #{searchVo.keyword}|| '%'
and USER_ID like '%'|| #{searchVo.keyword}|| '%'
and USER_EMAIL like '%'|| #{searchVo.keyword}|| '%'
)S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
)S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT CATEGORY_CODE2, CATEGORY_NAME, CATEGORY_CODE1
FROM TBL_PRODUCT_CATEGORY2
WHERE CATEGORY_CODE1 = #{categoryCode1}

SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_SELLER, CATEGORY_NAME, TO_CHAR(PRODUCT_REGISTER_DATE, 'yyyy-mm-dd') PRODUCT_REGISTER_DATE, FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_NUMBER FROM ( SELECT ROWNUM AS RNUM, PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_SELLER, CATEGORY_NAME, PRODUCT_REGISTER_DATE, FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_NUMBER FROM ( SELECT P.PRODUCT_NUMBER, P.PRODUCT_NAME, P.PRODUCT_PRICE, P.PRODUCT_SELLER, C.CATEGORY_NAME, P.PRODUCT_REGISTER_DATE, F.FILE_NAME, F.FILE_UPLOAD_PATH, F.FILE_UUID, F.FILE_NUMBER FROM TBL_PRODUCT P LEFT JOIN TBL_PRODUCT_CATEGORY2 C ON P.CATEGORY_CODE2 = C.CATEGORY_CODE2 LEFT JOIN ( SELECT FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_NUMBER, PRODUCT_NUMBER FROM ( SELECT FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_NUMBER, PRODUCT_NUMBER, RANK() OVER (PARTITION BY PRODUCT_NUMBER ORDER BY FILE_NUMBER) RK FROM TBL_PRODUCT_FILE ) WHERE RK = 1 ) F ON P.PRODUCT_NUMBER = F.PRODUCT_NUMBER
TO_CHAR(P.PRODUCT_REGISTER_DATE, 'yyyy-mm-dd') >= #{searchProductVo.startDate} AND #{searchProductVo.endDate} >= TO_CHAR(P.PRODUCT_REGISTER_DATE, 'yyyy-mm-dd')
AND PRODUCT_NAME like '%'|| #{searchProductVo.keyword}|| '%'
AND P.PRODUCT_NUMBER like '%'|| #{searchProductVo.keyword2}|| '%'
AND PRODUCT_SELLER like '%'|| #{searchProductVo.keyword}|| '%'
AND C.CATEGORY_NAME = #{searchProductVo.productType}
) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT COUNT(DISTINCT P.PRODUCT_NUMBER) FROM TBL_PRODUCT P LEFT JOIN TBL_PRODUCT_CATEGORY2 C ON P.CATEGORY_CODE2 = C.CATEGORY_CODE2 LEFT JOIN TBL_PRODUCT_FILE F ON P.PRODUCT_NUMBER = F.PRODUCT_NUMBER
TO_CHAR(P.PRODUCT_REGISTER_DATE, 'yyyy-mm-dd') >= #{searchProductVo.startDate} and #{searchProductVo.endDate} >= TO_CHAR(P.PRODUCT_REGISTER_DATE, 'yyyy-mm-dd')
AND PRODUCT_NAME like '%'|| #{searchProductVo.keyword}|| '%'
AND P.PRODUCT_NUMBER = #{searchProductVo.keyword2}
AND PRODUCT_SELLER like '%'|| #{searchProductVo.keyword}|| '%'
C.CATEGORY_NAME = #{searchProductVo.productType}

SELECT COMPANY_NUMBER,COMPANY_REGISTRATION_NUMBER, COMPANY_NAME, COMPANY_ID, COMPANY_CALL_NUMBER, COMPANY_CEO_NAME, COMPANY_ADDRESS1, COMPANY_EMAIL, COMPANY_STATUS FROM( SELECT ROWNUM AS RNUM,COMPANY_NUMBER, COMPANY_REGISTRATION_NUMBER, COMPANY_NAME, COMPANY_ID, COMPANY_CALL_NUMBER, COMPANY_CEO_NAME, COMPANY_ADDRESS1, COMPANY_EMAIL, COMPANY_STATUS FROM( SELECT COMPANY_NUMBER, COMPANY_REGISTRATION_NUMBER, COMPANY_NAME, COMPANY_ID, COMPANY_CALL_NUMBER, COMPANY_CEO_NAME, COMPANY_ADDRESS1, COMPANY_EMAIL, COMPANY_STATUS FROM TBL_COMPANY
and COMPANY_NAME like '%'|| #{searchCompanyVo.keyword}|| '%'
and COMPANY_ID like '%'|| #{searchCompanyVo.keyword}|| '%'
and COMPANY_EMAIL like '%'|| #{searchCompanyVo.keyword}|| '%'
COMPANY_STATUS = 1
COMPANY_STATUS = 0
)S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
)S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT TO_CHAR(ORDER_DATE, 'yyyy-mm-dd') ORDER_DATE, ORDER_NUMBER, USER_NAME, PRODUCT_SELLER, PRODUCT_NAME, PRODUCT_PRICE, ORDER_COUNT, CONDITION_CODE_NAME, ORDER_CONDITION_CODE FROM (SELECT ROWNUM AS RNUM, ORDER_DATE, ORDER_NUMBER, USER_NAME, PRODUCT_SELLER, PRODUCT_NAME, PRODUCT_PRICE, ORDER_COUNT, CONDITION_CODE_NAME, ORDER_CONDITION_CODE FROM ( SELECT O.ORDER_DATE, O.ORDER_NUMBER, U.USER_NAME, P.PRODUCT_SELLER, P.PRODUCT_NAME, P.PRODUCT_PRICE, O.ORDER_COUNT, OC.CONDITION_CODE_NAME, O.ORDER_CONDITION_CODE
FROM TBL_ORDER O JOIN TBL_PRODUCT P ON O.PRODUCT_NUMBER = P.PRODUCT_NUMBER JOIN TBL_USER U ON O.USER_NUMBER = U.USER_NUMBER JOIN TBL_ORDER_CONDITION OC ON O.ORDER_CONDITION_CODE = OC.ORDER_CONDITION_CODE
TO_CHAR(O.ORDER_DATE, 'yyyy-mm-dd') >= #{searchProductVo.startDate} and #{searchProductVo.endDate} >= TO_CHAR(O.ORDER_DATE, 'yyyy-mm-dd')
AND USER_NAME like '%'|| #{searchProductVo.keyword}|| '%'
AND PRODUCT_NAME like '%'|| #{searchProductVo.keyword}|| '%'
AND CONDITION_CODE_NAME like '%'|| #{searchProductVo.keyword}|| '%'
)S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
)S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount} ORDER BY ORDER_NUMBER

mapper.update:
UPDATE TBL_ORDER
SET ORDER_CONDITION_CODE = #{orderConditionCode}
WHERE ORDER_NUMBER = #{orderNumber}

update TBL_COMPANY
set COMPANY_STATUS = #{companyStatus}
where COMPANY_NUMBER = #{companyNumber}

mapper.select:
