mapper.select:
SELECT CONSULTING_REQUEST_NUMBER, USER_NUMBER, COMPANY_NUMBER,REVIEW_NUMBER, CONSULTING_REQUEST_DATE ,CONSULTING_COMMENT, CONSULTING_REQUEST_COMMENT, FILE_NAME, FILE_UPLOAD_PATH, REVIEW_DATE, REVIEW_CONTENT, REVIEW_GRADE FROM (SELECT ROWNUM AS RNUM,CONSULTING_REQUEST_NUMBER, USER_NUMBER, COMPANY_NUMBER,REVIEW_NUMBER, CONSULTING_REQUEST_DATE ,CONSULTING_COMMENT, CONSULTING_REQUEST_COMMENT, FILE_NAME, FILE_UPLOAD_PATH, REVIEW_DATE, REVIEW_CONTENT, REVIEW_GRADE FROM (SELECT CQ.CONSULTING_REQUEST_NUMBER, CQ.USER_NUMBER, CQ.COMPANY_NUMBER,CR.REVIEW_NUMBER, CQ.CONSULTING_REQUEST_DATE ,C.CONSULTING_COMMENT, CQ.CONSULTING_REQUEST_COMMENT, CF.FILE_NAME, CF.FILE_UPLOAD_PATH, CR.REVIEW_DATE, CR.REVIEW_CONTENT, CR.REVIEW_GRADE FROM TBL_CONSULTING_REQUEST CQ JOIN TBL_CONSULTING C ON CQ.CONSULTING_REQUEST_NUMBER = C.CONSULTING_REQUEST_NUMBER LEFT JOIN TBL_COMPANY_FILE CF ON CQ.COMPANY_NUMBER = CF.COMPANY_NUMBER JOIN TBL_CONSULTING_REVIEW CR ON CQ.CONSULTING_REQUEST_NUMBER = CR.CONSULTING_REQUEST_NUMBER WHERE C.USER_NUMBER = #{userNumber} ORDER BY CQ.CONSULTING_REQUEST_DATE DESC) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

mapper.update:
update TBL_CONSULTING_REVIEW
SET  REVIEW_DATE = SYSDATE,REVIEW_CONTENT = #{reviewContent}, REVIEW_GRADE = #{reviewGrade}
WHERE REVIEW_NUMBER = #{reviewNumber}

mapper.select:
SELECT USER_NUMBER, CONSULTING_REQUEST_NUMBER, COMPANY_NUMBER, FILE_NAME, FILE_UPLOAD_PATH, COMPANY_NAME, CONSULTING_REQUEST_COMMENT, CONSULTING_REQUEST_DATE, COMPANY_CEO_NAME, CONSULTING_CONDITION FROM (SELECT ROWNUM AS RNUM, USER_NUMBER, CONSULTING_REQUEST_NUMBER, COMPANY_NUMBER, FILE_NAME, FILE_UPLOAD_PATH, COMPANY_NAME, CONSULTING_REQUEST_COMMENT, CONSULTING_REQUEST_DATE, COMPANY_CEO_NAME, CONSULTING_CONDITION FROM (SELECT CR.USER_NUMBER, CR.CONSULTING_REQUEST_NUMBER, CM.COMPANY_NUMBER, CF.FILE_NAME, CF.FILE_UPLOAD_PATH, CM.COMPANY_NAME, CR.CONSULTING_REQUEST_COMMENT, CR.CONSULTING_REQUEST_DATE, CM.COMPANY_CEO_NAME, CR.CONSULTING_CONDITION FROM TBL_COMPANY CM JOIN TBL_CONSULTING_REQUEST CR ON CM.COMPANY_NUMBER = CR.COMPANY_NUMBER LEFT JOIN TBL_COMPANY_FILE CF ON CF.COMPANY_NUMBER = CM.COMPANY_NUMBER WHERE CR.USER_NUMBER = #{userNumber} ORDER BY CR.CONSULTING_REQUEST_DATE DESC ) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

mapper.update:
UPDATE TBL_CONSULTING_REQUEST
SET CONSULTING_CONDITION = 2
WHERE CONSULTING_REQUEST_NUMBER = #{consultingRequestNumber}

mapper.select:
SELECT ORDER_NUMBER, USER_NUMBER, PRODUCT_NUMBER, ORDER_COUNT, ORDER_DATE, ORDER_CONDITION_CODE, CONDITION_CODE_NAME, PRODUCT_NAME, PRODUCT_SELLER, PRODUCT_EXPLANATION, PRODUCT_PRICE, FILE_NAME, FILE_UPLOAD_PATH FROM(SELECT ROWNUM AS RNUM,ORDER_NUMBER, USER_NUMBER, PRODUCT_NUMBER, ORDER_COUNT, ORDER_DATE, ORDER_CONDITION_CODE, CONDITION_CODE_NAME, PRODUCT_NAME, PRODUCT_SELLER, PRODUCT_EXPLANATION, PRODUCT_PRICE, FILE_NAME, FILE_UPLOAD_PATH FROM(SELECT O.ORDER_NUMBER, O.USER_NUMBER, O.PRODUCT_NUMBER,O.ORDER_COUNT, O.ORDER_DATE, OC.ORDER_CONDITION_CODE, OC.CONDITION_CODE_NAME, P.PRODUCT_NAME, P.PRODUCT_SELLER, P.PRODUCT_EXPLANATION, P.PRODUCT_PRICE, F.FILE_NAME, F.FILE_UPLOAD_PATH FROM TBL_ORDER O JOIN TBL_ORDER_CONDITION OC ON O.ORDER_CONDITION_CODE = OC.ORDER_CONDITION_CODE JOIN TBL_PRODUCT P ON O.PRODUCT_NUMBER = P.PRODUCT_NUMBER LEFT JOIN (SELECT FILE_NAME, FILE_UPLOAD_PATH, PRODUCT_NUMBER FROM ( SELECT FILE_NAME,FILE_UPLOAD_PATH,PRODUCT_NUMBER, RANK() OVER (PARTITION BY PRODUCT_NUMBER ORDER BY FILE_NUMBER) RK FROM TBL_PRODUCT_FILE ) WHERE RK = 1 ) F ON O.PRODUCT_NUMBER = F.PRODUCT_NUMBER WHERE USER_NUMBER = #{userNumber} ORDER BY O.ORDER_DATE DESC )S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

mapper.update:
UPDATE TBL_ORDER
SET ORDER_CONDITION_CODE = 13
WHERE ORDER_NUMBER = #{orderNumber}

update TBL_ORDER_REVIEW
SET  REVIEW_DATE = SYSDATE,REVIEW_CONTENT = #{reviewContent}, REVIEW_GRADE = #{reviewGrade}
WHERE REVIEW_NUMBER = #{reviewNumber}

mapper.select:
SELECT COUNT(REVIEW_NUMBER)
FROM TBL_CONSULTING_REVIEW
WHERE USER_NUMBER = #{userNumber}
